  const noticia = noticias.find((n) => n.id === params.id);
  if (!noticia) {
    return {
      title: 'Noticia no encontrada',
    };
  }
  return {
    title: `${noticia.title} | Club Libertad`,
    description: noticia.extract,
  };
}


